contracts/L1TokenBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.16;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@scroll-tech/contracts/L2/gateways/IL2ERC20Gateway.sol";
import "@scroll-tech/contracts/L1/IL1ScrollMessenger.sol";

contract L1TokenBridge is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // События
    event DepositERC20(
        address indexed l1Token,
        address indexed l2Token,
        address indexed from,
        address to,
        uint256 amount,
        bytes data
    );

    event FinalizeWithdrawERC20(
        address indexed l1Token,
        address indexed l2Token,
        address indexed from,
        address to,
        uint256 amount,
        bytes data
    );

    // Адреса токенов L1 и L2
    address public l1Token;
    address public l2Token;

    // Адреса системных контрактов Scroll
    address public counterpart;
    address public router;
    address public messenger;

    // Инициализация 
    bool private _initialized;

    constructor() {}

    // Инициализация моста (вызывается один раз после деплоя)
    function initialize(
        address _counterpart, 
        address _router, 
        address _messenger,
        address _l1Token,
        address _l2Token
    ) external onlyOwner {
        require(!_initialized, "L1TokenBridge: already initialized");
        require(_router != address(0), "L1TokenBridge: zero router address");
        require(_messenger != address(0), "L1TokenBridge: zero messenger address");
        require(_counterpart != address(0), "L1TokenBridge: zero counterpart address");
        require(_l1Token != address(0), "L1TokenBridge: zero l1 token address");
        require(_l2Token != address(0), "L1TokenBridge: zero l2 token address");

        counterpart = _counterpart;
        router = _router;
        messenger = _messenger;
        l1Token = _l1Token;
        l2Token = _l2Token;

        _initialized = true;
    }

    // Возвращает адрес L2 токена для соответствующего L1 токена
    function getL2ERC20Address(address _l1Token) public view returns (address) {
        require(_l1Token == l1Token, "L1TokenBridge: invalid token");
        return l2Token;
    }

    // Публичный метод для отправки токенов через мост
    function bridgeToken(uint256 _amount, uint256 _gasLimit) external payable nonReentrant {
        require(msg.value > 0, "L1TokenBridge: need ETH for fees");
        require(_amount > 0, "L1TokenBridge: bridge zero amount");
        _deposit(msg.sender, _amount, _gasLimit);
    }

    // Финализирует вывод токена с L2 на L1
    function finalizeWithdrawERC20(
        address _l1Token,
        address _l2Token,
        address _from,
        address _to,
        uint256 _amount,
        bytes calldata _data
    ) external payable {
        require(msg.sender == messenger, "L1TokenBridge: not from messenger");
        require(IL1ScrollMessenger(messenger).xDomainMessageSender() == counterpart, "L1TokenBridge: not from counterpart");
        require(_l1Token == l1Token, "L1TokenBridge: invalid l1 token");
        require(_l2Token == l2Token, "L1TokenBridge: invalid l2 token");

        // Отправляем токены получателю
        IERC20(_l1Token).safeTransfer(_to, _amount);

        emit FinalizeWithdrawERC20(_l1Token, _l2Token, _from, _to, _amount, _data);
    }

    // Внутренняя функция для депозита токена с L1 на L2
    function _deposit(
        address _to,
        uint256 _amount,
        uint256 _gasLimit
    ) internal {
        // 1. Перевод токена на контракт моста
        IERC20(l1Token).safeTransferFrom(msg.sender, address(this), _amount);

        // 2. Генерация сообщения для L2CustomERC20Gateway
        bytes memory _message = abi.encodeCall(
            IL2ERC20Gateway.finalizeDepositERC20,
            (l1Token, l2Token, msg.sender, _to, _amount, new bytes(0))
        );

        // 3. Отправка сообщения в L1ScrollMessenger
        IL1ScrollMessenger(messenger).sendMessage{ value: msg.value }(counterpart, 0, _message, _gasLimit, msg.sender);

        emit DepositERC20(l1Token, l2Token, msg.sender, _to, _amount, new bytes(0));
    }
}

contracts/L2CustomToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.16;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@scroll-tech/contracts/libraries/token/IScrollERC20Extension.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

// Добавляем интерфейс IL2StandardERC20 для лучшей совместимости с Scroll
interface IL2StandardERC20 {
    function l1Token() external view returns (address);
    function mint(address account, uint256 amount) external;
    function burn(address account, uint256 amount) external;
}

contract L2CustomToken is ERC20, IScrollERC20Extension, Ownable {
    // Адрес моста (gateway) и адрес соответствующего L1 токена
    address private _gateway;
    address private _counterpart;

    constructor(address gateway_, address counterpart_) ERC20("sut_test_token_bonch_l2", "sut2") {
        _gateway = gateway_;
        _counterpart = counterpart_;
    }

    // Возвращает адрес шлюза
    function gateway() public view override returns (address) {
        return _gateway;
    }

    // Возвращает адрес соответствующего токена на L1
    function counterpart() external view override returns (address) {
        return _counterpart;
    }

    // Псевдоним для counterpart() для совместимости с IL2StandardERC20
    function l1Token() external view returns (address) {
        return _counterpart;
    }

    // Устанавливает новый адрес шлюза (только владелец)
    function setGateway(address newGateway) external onlyOwner {
        require(newGateway != address(0), "L2CustomToken: zero gateway address");
        _gateway = newGateway;
    }

    // Устанавливает новый адрес соответствующего токена (только владелец)
    function setCounterpart(address newCounterpart) external onlyOwner { 
        require(newCounterpart != address(0), "L2CustomToken: zero counterpart address");
        _counterpart = newCounterpart;
    }

    // Реализация transferAndCall для IScrollERC20Extension
    function transferAndCall(address receiver, uint256 amount, bytes calldata data) 
        external 
        override 
        returns (bool success) 
    {
        transfer(receiver, amount);
        return true;
    }

    // Эмиссия токенов (только для шлюза)
    function mint(address to, uint256 amount) external override onlyGateway {
        _mint(to, amount);
    }

    // Сжигание токенов (только для шлюза)
    function burn(address from, uint256 amount) external override onlyGateway {
        _burn(from, amount);
    }

    // Модификатор, ограничивающий вызов только для шлюза
    modifier onlyGateway() {
        require(gateway() == _msgSender(), "L2CustomToken: caller is not the gateway");
        _;
    }
}

contracts/L2TokenBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.16;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@scroll-tech/contracts/L1/gateways/IL1ERC20Gateway.sol";
import "@scroll-tech/contracts/L2/IL2ScrollMessenger.sol";
import "@scroll-tech/contracts/libraries/token/IScrollERC20Extension.sol";

contract L2TokenBridge is Ownable, ReentrancyGuard {
    // События
    event WithdrawERC20(
        address indexed l1Token,
        address indexed l2Token,
        address indexed from,
        address to,
        uint256 amount,
        bytes data
    );

    event FinalizeDepositERC20(
        address indexed l1Token,
        address indexed l2Token,
        address indexed from,
        address to,
        uint256 amount,
        bytes data
    );

    // Адреса токенов L1 и L2
    address public l1Token;
    address public l2Token;

    // Адреса системных контрактов Scroll
    address public counterpart;
    address public router;
    address public messenger;

    // Инициализация 
    bool private _initialized;

    constructor() {}

    // Инициализация моста (вызывается один раз после деплоя)
    function initialize(
        address _counterpart,
        address _router,
        address _messenger,
        address _l1Token,
        address _l2Token
    ) external onlyOwner {
        require(!_initialized, "L2TokenBridge: already initialized");
        require(_router != address(0), "L2TokenBridge: zero router address");
        require(_messenger != address(0), "L2TokenBridge: zero messenger address");
        require(_counterpart != address(0), "L2TokenBridge: zero counterpart address");
        require(_l1Token != address(0), "L2TokenBridge: zero l1 token address");
        require(_l2Token != address(0), "L2TokenBridge: zero l2 token address");

        counterpart = _counterpart;
        router = _router;
        messenger = _messenger;
        l1Token = _l1Token;
        l2Token = _l2Token;

        _initialized = true;
    }

    // Возвращает адрес L1 токена для соответствующего L2 токена
    function getL1ERC20Address(address _l2Token) public view returns (address) {
        require(_l2Token == l2Token, "L2TokenBridge: invalid token");
        return l1Token;
    }

    // Публичный метод для отправки токенов через мост обратно на L1
    function bridgeToken(uint256 _amount, uint256 _gasLimit) external payable nonReentrant {
        require(_amount > 0, "L2TokenBridge: bridge zero amount");
        require(msg.value > 0, "L2TokenBridge: need ETH for fees"); // Проверка наличия ETH для комиссии

        // Упрощенный вызов без проверки на роутер
        _withdraw(msg.sender, msg.sender, _amount, _gasLimit, new bytes(0));
    }

    // Финализирует депозит токена с L1 на L2
    function finalizeDepositERC20(
        address _l1Token,
        address _l2Token,
        address _from,
        address _to,
        uint256 _amount,
        bytes calldata _data
    ) external {
        require(msg.sender == messenger, "L2TokenBridge: not from messenger");
        require(IL2ScrollMessenger(messenger).xDomainMessageSender() == counterpart, "L2TokenBridge: not from counterpart");
        require(_l1Token == l1Token, "L2TokenBridge: invalid l1 token");
        require(_l2Token == l2Token, "L2TokenBridge: invalid l2 token");

        // Минтим токены получателю
        IScrollERC20Extension(l2Token).mint(_to, _amount);

        emit FinalizeDepositERC20(_l1Token, _l2Token, _from, _to, _amount, _data);
    }

    // Внутренняя функция для вывода токена с L2 на L1
    function _withdraw(
        address _from,
        address _to,
        uint256 _amount,
        uint256 _gasLimit,
        bytes memory _data
    ) internal {
        // Сжигаем токены на L2
        IScrollERC20Extension(l2Token).burn(_from, _amount);

        // Генерируем сообщение для L1ERC20Gateway
        bytes memory _message = abi.encodeCall(
            IL1ERC20Gateway.finalizeWithdrawERC20,
            (l1Token, l2Token, _from, _to, _amount, _data)
        );

        // Отправляем сообщение в L2ScrollMessenger, передавая всю msg.value
        IL2ScrollMessenger(messenger).sendMessage{ value: msg.value }(
            counterpart,
            msg.value,
            _message,
            _gasLimit
        );

        emit WithdrawERC20(l1Token, l2Token, _from, _to, _amount, _data);
    }
}

